/*  TEST2.js

    this is a class comp, updating state with button click.
    code from modules 2 and 3

    To setState Mod 2/video 2-2-2017-06-13-77241-dev281x_2017 1080 Encoding Preset.mp4
    state >  setSate with callb func.

*/
import React from 'react';
import { render } from '@testing-library/react';


/* React-Bootstrap Lib */
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

/* React-Icons  Lib  */
import { BsTrash, BsTrashFill, BsTrash2, BsTrash2Fill } from 'react-icons/bs';



class Test2 extends React.Component{
    constructor(props) {
        super(props)
        
        /*   State and event Bind   */
        this.state = {item:'', active:'true', ListArr: [] }
        this.handleItemChange = this.handleItemChange.bind(this)
        this.AddItem = this.AddItem.bind(this)
        this.DeleteItem = this.DeleteItem.bind(this)
        this.toggleComplete = this.toggleComplete.bind(this)
    }   
    
      /* OnClick ADD to do item to  ListArr  */  
      AddItem(event) {
        event.preventDefault()
        
        /*  create todo object from input  */
        const todoObj= {item:this.state.item, active:this.state.active}
        console.log("call func AddItem")
        console.log(todoObj)

        /*   copy array from state, push todo obj to ArrayCopy  */
        let ArrayCopy=[]
        ArrayCopy = Array.from(this.state.ListArr)
        ArrayCopy.push(todoObj)
        
        /*  Update state Array   */
        this.setState( {ListArr: ArrayCopy} , () => {
        console.log("state Arr after setState")
        console.log(this.state.ListArr);

       })
      }


      /* Input field UPDATE state  - this is a callback function */
      handleItemChange(event){
        this.setState( {item: event.target.value} , () => {

        console.log(this.state.item)
        })
      } 
      

      // Onclick DELETE to do item
      DeleteItem(index) {
        
        /*  event.preventDefault()    */
        /*  console.log("call DeleteItem func")
        console.log("props is")
        console.log(index)       */

        /*   copy array from state  */
        let getArray =[];
        getArray = Array.from(this.state.ListArr)

        console.log("this is getArray")
        console.log(getArray)

        /*  delete item at index   */
        getArray.splice(index,1)
        
       /*   update state   */
       this.setState( {ListArr: getArray} )

      }
      
      
    toggleComplete(index) {
    console.log("call toggleComplete")
    /* Toggle button value */
    if (button.value == "OFF") {
        button.value = "ON";

        /* copy state array, change 'active' value   */
        let ArrayCopy=[]
        ArrayCopy = Array.from(this.state.ListArr)
        ArrayCopy[index] = this.state.active = 'false'

        /* update state array  */
        this.setState( {ListArr: ArrayCopy} )
        


    } 
    
    else {
      button.value = "OFF";
    }
    }  

    
    
    
    
    /*   ****          RENDER            ****   */
      render(){
        return (
		<div> 
    <Container>
		<br/>
		<p> This is Test2 func, from Test2.js </p>
		<p> react is <b>complete</b> junk </p>
    
    {/* DISPLAY  Input field / Button  */}
    <div>
        <input type= 'text'
              value= {this.state.item}
              onChange= {this.handleItemChange}  />
    </div>        

    <div>             
        <button onClick={this.AddItem}> Add Item </button> 
    </div> <br/> <br/>


    {/*  DISPLAY  ToDo List  */}
    <div> 
     
     { this.state.ListArr.map( (element, index) => {
           /* let ItemId={index}; 
           console.log("this is ItemId");
           console.log( {ItemId} );         */
        return (
        <div>
          <Row>
          <Col style={{border:'1px dotted red', padding:'4px'}}> </Col>
          
          <Col style={{border:'1px dotted green', padding:'4px'}} > 
              
               <li key={index}>  {element.item}  </li>
          </Col>

          <Col style={{border:'1px dotted darkorange', padding:'4px'}}> </Col>

          <Col xs={1} style={{border:'1px dotted green', padding:'4px'}}> 
          <button onClick={ ()=> this.toggleComplete(index) } > act </button>
          </Col>

          <Col xs={1} style={{border:'1px dotted red', padding:'4px'}}> 
          <button onClick={ ()=> this.DeleteItem(index) } > del   </button>
           </Col>

          <br/><br/><br/>
          </Row>
        </div>
        )
    } ) 
    }
    </div>
    

		</Container>
		</div>
		)
    }
}
export default Test2;
