/*  ToDo.js  */ 
import React from 'react';
import {useState} from 'react';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
/* import Button from 'react-bootstrap/Button'  */




function ToDo(props) {
    {/*  STATE VARS  */}
    const [InputItem, setInputItem] = useState( {item:'', active:'true'} ) 
    const [List, setList] = useState([])  

    
    {/* FUNC  -  ADD input to setList/List array */}
    const addTheItem =  (e) => { 
         
        e.preventDefault()
        {/*}
        {/* setList(oldArray => [...oldArray, InputItem] ) 
        setList(oldArray => [...oldArray, InputItem] )        */}
  
    }     
    
    return (
        
        <div>
        <Container>
        
            <br/><br/>

          {/* INPUT BOX, BUTTON */}
          <div>   
            <input type='text' 
                   value={InputItem.item}
                   onChange= { e=> setInputItem({...InputItem, item:e.target.value}) }
            />
                    
            <button onClick={addTheItem}> Add the Item </button>  <br/> <br/>
          </div> 
            
            {JSON.stringify(List)}  <br/>  <br/>

            
            {/*   OUTPUT List  */}
            { List.map( (listElement, idx: number) => {
                  return (
                  <div>
                  <Row>
                    <Col style={{border:'1px dotted red', padding:'4px'}}> </Col>
                    
                    <Col style={{border:'1px dotted green', padding:'4px'}} > 
                    <li key={idx}>  {listElement} </li>
                    </Col>
                    
                    <Col style={{border:'1px dotted red', padding:'4px'}}> </Col>
                  </Row>   
                  </div>
                  )
                  })
                }
        </Container>
        
    
        </div>
    
    )
  }
  
  export default ToDo;
