/*  ToDo2.js  */ 
import React from 'react';
import {useState} from 'react';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
/* import Button from 'react-bootstrap/Button'  */

import Counter from './Test.js';



function ToDo(props) {
    {/*  STATE VARS  */}
    const [InputItem, setInputItem] = useState( {item:'', active:'true'} ) 
    const [List, setList] = useState([])  

    
    {/* FUNC  -  ADD input to setList/List array */}
    const addTheItem = async () => { 
         
        {/* setList(oldArray => [...oldArray, InputItem] )  */}
        setList(oldArray => [...oldArray, InputItem] )
    }   /* may need (props)  */
    
    return (
        
        <div>
        <Container>
        
            <br/> <p> Todo2.js </p> <br/>

          {/* INPUT BOX, BUTTON */}
          <div>   
            <input 
            type='text' 
            onChange ={ (event: ChangeEvent<HTMLInputElement>) => {
            setInputItem( event.currentTarget.value )  }
            } />
                    
            <button onClick={addTheItem}> Add the Item </button>  <br/> <br/>
          </div> 
            
            {JSON.stringify(List)}  <br/>  <br/>

            
            {/*   OUTPUT List  */}
            { List.map( (listElement, idx: number) => {
                  return (
                  <div>
                  <Row>
                    <Col style={{border:'1px dotted red', padding:'4px'}}> </Col>
                    
                    <Col style={{border:'1px dotted green', padding:'4px'}} > 
                    <li key={idx}>  {listElement} </li>
                    </Col>
                    
                    <Col style={{border:'1px dotted red', padding:'4px'}}> </Col>
                  </Row>   
                  </div>
                  )
                  })
                }
        </Container>
                <div>  <Counter /> </div>
    
        </div>
    
    )
  }
  
  export default ToDo;
