/*  ToDO.js  */ 
import React from 'react';
import {useState, useEffect} from 'react';




function ToDo(props) {

    const [currentItem, setCurrentItem] = useState('') 
    const [List, setList] = useState([])  

    const addTheItem = async () => {   
        
        setList(oldArray => [...oldArray, currentItem  ] )
      
      }
    
    return (
        <div>
            <br/><br/>

          {/* INPUT BOX, BUTTON */}
          <div>   
            <input type='text' onChange ={ (event: ChangeEvent<HTMLInputElement>) => {
            setCurrentItem(event.currentTarget.value) }
            } />
                    
            <button onClick={addTheItem}> Add the Item </button>  <br/> <br/>
          </div> 
            
            {JSON.stringify(List)}  <br/>  <br/>

            
            {/*   OUTPUT List  */}
              <div style={{ border:'1px solid red'}}>
                { List.map((listElement, idx: number) => {
                  return (
                  <div className='spacer' >  
                    <div style={{border:'1px solid blue', padding:'4px'}} > 
                      <li key={idx}>  {listElement}  </li>
                    </div>
                  </div>
                  )
                  })
                }
              </div>
        
        </div>
    
    )
  }
  
  export default ToDo;
