/*  
*   Test2.js   {from test2.js and from class 12-14-20
*   this is a class comp, updating state with button click
*/
import React from 'react';
import {useState, useEffect} from 'react';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';


class Test2 extends React.Component{
    constructor(props){
        super(props)
        this.state = {count:0}
        this.clickHandler = this.clickHandler.bind(this)
    }
    
    clickHandler(){
      //increments the count of the state
      this.setState((prevState,props) => {
        return {count: prevState.count + 1}
      })
    }
    
    render(){
        //renders a button that displays the state count
        return <button onClick = {this.clickHandler}> a button </button>
    }
  }


export default Test2;
